def can_form_molecule(a, b, c):
    total_valence = a + b + c
    if total_valence % 2 != 0:
        return "Impossible"
    target_bond_count = total_valence // 2
    if a <= target_bond_count and b <= target_bond_count and c <= target_bond_count:
        return f"{a} {b} {c}"
    elif a + b <= target_bond_count and c <= target_bond_count:
        return f"{a} {b} {c}"
    elif a + c <= target_bond_count and b <= target_bond_count:
        return f"{a} {b} {c}"
    else:
        return "Impossible"

