MOD = 10**9 + 7

def count_polylines(n, t):
    dp = [[[[0 for _ in range(2)] for _ in range(t + 1)] for _ in range(t + 1)] for _ in range(n + 1)]

    for j in range(1, 5):
        dp[1][j][0][0] = 1

    for i in range(2, n + 1):
        for j in range(1, 5):
            for k in range(t + 1):
                for l in range(t):
                    if k > 0:
                        dp[i][j][k][l] += dp[i-1][j-1][k-1][l]
                        dp[i][j][k][l] %= MOD
                    if l > 0:
                        dp[i][j][k][l] += dp[i-1][j+1][k][l-1]
                        dp[i][j][k][l] %= MOD
                    dp[i][j][k][l] += dp[i-1][j][k][l]
                    dp[i][j][k][l] %= MOD

    result = 0
    for j in range(1, 5):
        for k in range(t + 1):
            for l in range(t):
                result += dp[n][j][k][l]
                result %= MOD

    return result

